import asyncio
import json
import ssl
import websockets
import os
import aiofiles
from web3 import Web3
import time


address = "0x10ED43C718714eb63d5aA57B78B54704E256024E" #pancakeSwap: router v2
TARGET_TO_ADDRESS = address.lower()

lock = asyncio.Lock()

async def process_next_notification(notification,contract):
    try:
        notification_data = json.loads(notification)
        to_address = notification_data.get("params", {}).get("result", {}).get("txContents", {}).get("to")


        if to_address == TARGET_TO_ADDRESS:
            input_data = notification_data.get("params", {}).get("result", {}).get("txContents", {}).get("input")
            # Decode the transaction input
            func_obj, func_params = contract.decode_function_input(input_data)
            print(func_obj.fn_name)
            if func_obj.fn_name == 'swapExactETHForTokens':
                print("Matched! Processing and saving notification:", notification)
                # Add the notification to the JSON file
                notification_data['time'] = time.time()

                file_path = '../BSC_bundle_submission/data.json'

                async with lock:
                    # Ensure the file exists and create it if not
                    if not os.path.exists(file_path):
                        async with aiofiles.open(file_path, 'w') as f:
                            await f.write(json.dumps([]))

                    # Read the existing data
                    async with aiofiles.open(file_path, 'r') as f:
                        file_content = await f.read()
                        data = json.loads(file_content) if file_content else []

                    # Append the new notification
                    data.append(notification_data)

                    # Write the updated data back to the file
                    async with aiofiles.open(file_path, 'w') as f:
                        await f.write(json.dumps(data, indent=4))
    except json.JSONDecodeError:
        print("Failed to decode notification as JSON:", notification)

