    contract = web3.eth.contract(address=address_checksum, abi=abi)

    async with websockets.connect(
            uri,
            extra_headers={"Authorization": headers_auth_key},
            ssl=ssl_context
    ) as websocket:

        # ETH Example
        subscribe_request = {
            "jsonrpc": "2.0",
            "id": 1,
            "method": "subscribe",
            "params": ["newTxs", {"include": ["tx_hash", "tx_contents", "raw_tx"]}]
        }

        await websocket.send(json.dumps(subscribe_request))
        response = await websocket.recv()
        subscription_id = json.loads(response)["result"]
        print(f"Subscribed successfully with subscription_id {subscription_id}")

        while True:
            try:
                next_notification = await websocket.recv()
                # Process the notification in the background
                asyncio.create_task(process_next_notification(next_notification,contract))
            except websockets.exceptions.ConnectionClosedError:
                print("Connection closed unexpectedly")

